INSERT INTO `fortune poems` (`poem_id`, `poem_no`, `poem_url`, `poem_sign`, `poem_text`, `poem_meaning`) VALUES
(1, '第一支籤', '001', '大吉', '<h5 class=\"text-center tm-text-gray-dark\">七寶浮圖塔</h6><h6 class=\"text-center tm-text-gray\">就像出現了用美麗寶石做成的佛塔般地，似乎會有非常好的事情。</h6><h5 class=\"text-center tm-text-gray-dark\">高峰頂上安</h5><h6 class=\"text-center tm-text-gray\">因為能改用放眼萬事的立場，可以得到周圍的人們的信賴吧。</h6><h5 class=\"text-center tm-text-gray-dark\">眾人皆仰望</h5><h6 class=\"text-center tm-text-gray\">合乎正道的你的行為，能被很多人的認同及鼓勵。</h6><h5 class=\"text-center tm-text-gray-dark\">莫作等閒看</h5><h6 class=\"text-center tm-text-gray\">不用隨便的態度看事情，用正確的心思會招來更多的好的結果。</h6>', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會充分地實現吧。</span></div>\r\n<div class=\"mr-4 mb-2\"><span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治癒吧。</span></div><div class=\"mr-4 mb-2\"><span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span></div><div class=\"mr-4 mb-2\"><span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">變得遲遲地才找到吧。</span></div><div class=\"mr-4 mb-2\"><span class=\"tm-text-gray-dark\">蓋新居、搬家、嫁娶、旅行、交往等：</span><span class=\"tm-text-primary\">全部很好吧。</span></div><div class=\"mr-4 mb-2\"><span class=\"tm-text-gray-dark\">祝福語：</span><span class=\"tm-text-primary\">萬事行為謹慎。粗心大意行事的話，就會發生意想之外的災害吧。</span></div>'),
(2, '第二支籤', '002', '小吉', '<h5 class=\"text-center tm-text-gray-dark\">月被浮雲翳</h6>\r\n<h6 class=\"text-center tm-text-gray\">似乎抱著強烈的願望，但是照目前的樣子，似乎無法達成願望。</h6>\r\n<h5 class=\"text-center tm-text-gray-dark\">立事自昏迷</h5>\r\n<h6 class=\"text-center tm-text-gray\">因為光是想著要怎麼作，持續著沒有決心的情形。</h6>\r\n<h5 class=\"text-center tm-text-gray-dark\">幸乞隂公祐</h5>\r\n<h6 class=\"text-center tm-text-gray\">為了人而變得盡全力努力，幸福將會來到。</h6>\r\n<h5 class=\"text-center tm-text-gray-dark\">何慮不開眉</h5>\r\n<h6 class=\"text-center tm-text-gray\">似乎會有令人高興的事情發生。根據這件好事，不擔心未來的事也沒有關係了。</h6>', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">因為持續不斷地努力，必定會實現。</span>\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">雖然拖長，但是之後可以康復吧。</span>\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">遲遲地才出現吧。</span>\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">不能找出來吧。</span>\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">交往：</span><span class=\"tm-text-primary\">要節制吧。</span>\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">都不壞吧。</span>\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結親緣、旅行：</span><span class=\"tm-text-primary\">順利進行。</span>\r\n</div>'),
(3, '第三支籤', '003', '凶', '<h5 class=\"text-center tm-text-gray-dark\">愁惱損忠良</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">層層疊疊嘆氣與苦惱，被回報的事很少吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">青霄一炷香</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像向著天燒香祈禱般地，你的願望無法傳達天聽吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">雖然防小過</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">雖這樣說，但就算只有一點點善行也好，作了可以逃離災厄吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">閑慮覺時長</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">東想西想之間，似乎不知不覺就像過了很長的時間。等待時機的到來吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">雖然拖長，但是會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">要花很久的時間吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難以找到吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">因為很壞，放棄吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">勉勉強強地算好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">要節制吧。</span>\r\n</div>'),
(4, '第四支籤', '004', '吉', '<h5 class=\"text-center tm-text-gray-dark\">累有興雲志</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">拼命地要出人頭地，可以看出你的志向。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">君恩祿未封</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">但是遺憾地是，你的不成熟還不能得到居上位者的認同。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">若逢侯手印</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">然而，就像是如果已經寫了好文章的話，就立刻得到認同般地，好好傳遞自己的心思是很重要的。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">好事始總總</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">好事也似乎會越來越接踵而起吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">能實現吧。如果這樣的話，終生幸福吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">變得遲遲地才會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">遲遲地才找到吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">途中要忍耐各式各樣的困難吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家、結親緣、交往：</span><span class=\"tm-text-primary\">萬事都好吧。</span>\r\n\r\n</div>'),
(5, '第五支籤', '005', '凶', '<h5 class=\"text-center tm-text-gray-dark\">家道未能昌</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就算試著努力家業，和努力相比卻難以繁盛起來吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">危危保禍殃</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">不是一生災禍，只是危險的事比較多而已吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">暗雲侵月桂</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像烏雲遮月一樣，一生阻礙比較多吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">佳人一炷香</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">在身份高貴的婦人房裡，各種想法像香一樣擴散開來般，心裡無法平靜吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難以實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">難治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難找到吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">先放棄，暫時觀察情況再說吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結親緣、旅行、交往：</span><span class=\"tm-text-primary\">因為萬事兇惡，請諸行為慎重行事。</span>\r\n\r\n</div>'),
(6, '第六支籤', '006', '末吉', '<h5 class=\"text-center tm-text-gray-dark\">宅墓鬼凶多</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">家中恐怕有災禍。行為慎重，抱著深深的信心，這樣可以帶來好的結果。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">人事有爻訛</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">個人的問題或遭遇，過錯或過失很多，事情難以進展吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">傷財防損失</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就算破財，也會有所得。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">祈福始中和</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">因為竭盡祈求神佛的力量，自己盡力努力，能到幸福吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難以實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">康復很花時間吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">遲遲地才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難找到吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結親緣、交往：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>'),
(7, '第七支籤', '007', '凶', '<h5 class=\"text-center tm-text-gray-dark\">登舟待便風</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像風不吹船無法前進一樣，就算採取行動也難以向前發展吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">月色暗矇矓</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像月亮被烏雲籠罩著，前後都無法看見一樣，對事情，似乎作一些莽撞的行為。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">遇碾香輪去</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">為了逃離災難，想請託別人教導怎麼作，但是況狀似乎變得很困難。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">高山千萬重</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">但是現在還是靜靜的過生活吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難以實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">難以治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難以找到吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">換時間吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結親緣、喜慶祝賀、旅行、交往：</span><span class=\"tm-text-primary\">不好吧。</span>\r\n\r\n</div>'),
(8, '第八支籤', '008', '大吉', '<h5 class=\"text-center tm-text-gray-dark\">勿頭中見尾</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">在腦海裡沒有對失敗或結果的恐懼，抬頭挺胸地朝著目標達成而努力吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">文華須得理</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">無論是文學或武術都能得到真理，要有充實自己的心態吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">禾刀自偶然</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像用刀來割稻般地，會得到成果，幸福會自然地到來吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">當遇非常喜</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">如果正心地守道的話，可以變得幸福吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好。請注意養生吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">可以找到吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家、交往：</span><span class=\"tm-text-primary\">是好事吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">途中請不要粗心大意吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結親緣：</span><span class=\"tm-text-primary\">全都是好的吧。</span>\r\n\r\n</div>'),
(9, '第九支籤', '009', '大吉', '<h5 class=\"text-center tm-text-gray-dark\">有名須得遇</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">按照所想的，夢想實現，名聲也廣傳人間吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">三望一朝遷</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像三個願望可以完全的實現般地，全部能一次完全的被實現吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">貴人來指處</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">從比自己身份地位高的人，給予各式各樣令人欣喜的事吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">華菓應時鮮</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是隨著四季循環花會開、會結果一樣，每天努力會有成果，好運會展開吧。 </h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會找到吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">沒問題吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結親緣、交往：</span><span class=\"tm-text-primary\">全都好吧。</span>\r\n\r\n</div>'),
(10, '第十支籤', '010', '大吉', '<h5 class=\"text-center tm-text-gray-dark\">舊用多成破</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">過去的不幸或許多煩惱也會消去，好事將發生吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">新更始見財</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">隨著新的願望的出現，財富也會增加。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">改求雲外望</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像是在雲上追求願望般地，請試著追求高高的願望。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">枯木遇春開</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像枯木在春天開花一樣，一定會變得很繁盛吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">立刻會找到吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">會變為好結果吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結親緣、旅行、交往：</span><span class=\"tm-text-primary\">全部變為好結果吧。</span>\r\n\r\n</div>'),
(11, '第十一支籤', '011', '大吉', '<h5 class=\"text-center tm-text-gray-dark\">有祿興家業</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">幸福與收入具足，家業也漸漸繁盛起來吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">文華達帝都</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">才能顯現出來，可得世人的好評價吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">雲中乘好箭</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就算是向空中放箭也可以得到好的獵物般地，任何事也都可以成功吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">兼得貴人扶</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">得到這樣的幸福之外，加上還可以得到居上位者或神佛的幫助吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會充分地實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會找到吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">沒問題吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">全部都能得到好結果吧。</span>\r\n\r\n</div>'),
(12, '第十二支籤', '012', '大吉', '<h5 class=\"text-center tm-text-gray-dark\">楊柳遇春時</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像柳樹也逢春增添綠色般地，希望也會出現吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">殘花發舊枝</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像老枝也發芽，開花般地，喜事會到來吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">重重霜雪裡</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像在重重霜或雪中般地，無論怎樣過去的勞苦不斷吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">黃金色更輝</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">但是像黃金色無論到何時，都閃亮耀眼般地，不要忘以前的勞苦，用誠實的心過生活吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會變好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">晚出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會回來吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">會成為好結果吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">沒有阻礙吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">全都適當吧。</span>\r\n\r\n</div>'),
(13, '第十三支籤', '013', '大吉', '<h5 class=\"text-center tm-text-gray-dark\">手把大陽輝</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">如果時機來到的話，抱著勇氣努力做事，可以過著充實的生活吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">東君發舊枝</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像是老枝條，隨著春天到來，花就開了一樣，運勢會開展吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">稼苗方欲秀</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像稻苗也是如果春天來了的話，就會生長般地，你也會繁榮昌盛吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">猶更上雲梯</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是也能爬上難登的雲梯般，因為更加地持續努力就會成功吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">立刻找就能找到吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">在春天和夏天好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">全部都好吧。</span>\r\n\r\n</div>'),
(14, '第十四支籤', '014', '末吉', '<h5 class=\"text-center tm-text-gray-dark\">玉石未分時</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像是就算有寶玉，哪一個是寶石哪一個是石頭還分不清楚般地，沒辦法分辨事物的狀況。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">憂心轉更悲</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就算是想要出人頭地，但會為了各式各樣的事情而心痛、嘆息、悲傷也說不定。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">前途通大道</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">如果忍耐勞苦，將來自然地看得到未來的去向吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">花發應殘枝</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">然後像枯枝開花般地，願望會實現吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">很花時間但會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會拖長吧，但是不會影響性命吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">似乎會變得遲吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">不太好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行、交往：</span><span class=\"tm-text-primary\">避開吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚：</span><span class=\"tm-text-primary\">現在要節制，如果往後的話好吧。</span>\r\n\r\n</div>'),
(15, '第十五支籤', '015', '凶', '<h5 class=\"text-center tm-text-gray-dark\">年乖數亦孤</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">朋友年年地減少，變成孤單一人，生活變得不自由吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">久病未能蘇</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">長期的疾病雖然種種地想治癒，但是卻康復無望吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">岸危舟未發</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像船想靠岸，因為危險不能靠岸般地，想做點什麼，但是因為有阻礙，總是不能著手進行吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">龍臥失明珠</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是龍失去重要的龍珠般地，人也失去希望吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難以實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">危險吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難找回吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">都不好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行、結親緣：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>'),
(16, '第十六支籤', '016', '吉', '<h5 class=\"text-center tm-text-gray-dark\">破改重成望</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">拋開至今為止的願望，期待別的願望為佳吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">前途喜亦寧</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">要前往的目的地有令人欣喜的事，變為安穩的心情吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">貴人相助處</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">因為得到居上位者（觀世音菩薩）的幫助，越來越得到力量吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">祿馬照前程</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是用馬載著上天給予的寶物，前方光明照耀般地，在人世間地位或收入等也和想的一樣地充滿吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">能被實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會康復吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會來吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">會有好結果吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結親緣、交往：</span><span class=\"tm-text-primary\">全部會變成好結果吧。</span>\r\n\r\n</div>'),
(17, '第十七支籤', '017', '凶', '<h5 class=\"text-center tm-text-gray-dark\">怪異防憂惱</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">如果作一些想要防止降落在自己身上的危險的事的話，反而煩惱似乎變多了。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">人宅見分離</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">不知不覺間，不好的事情持續著，似乎會有家人分離的事。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">惜華還值雨</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像捨不得花凋謝的話，更是會被雨淋凋萎般地，不好的是似乎持續著。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">杯酒惹閑非</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">因為無法按照所想的發展，沈溺於喝酒之類的，似乎還是會產生壞想法吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">康復需要長時間吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">先放棄，暫時觀察情況再說吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">似乎引起壞的結果吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">婚事、交往：</span><span class=\"tm-text-primary\">全部不好吧。</span>\r\n\r\n</div>'),
(18, '第十八支籤', '018', '吉', '<h5 class=\"text-center tm-text-gray-dark\">離暗出明時</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像烏雲的天空也漸漸地晴朗，可以看到月亮般地，今後漸漸地希望能被實現吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">麻衣變綠衣</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像脫掉破舊的衣服，穿著漂亮的衣服般地，用全新的心情做好每天的善行吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">舊憂終是退</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">長時間的悲傷也漸漸地消散吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">遇祿應交輝</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">福德自然地增加，人生光輝閃耀吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實踐吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結親緣：</span><span class=\"tm-text-primary\">全部變為好結果吧。</span>\r\n\r\n</div>'),
(19, '第十九支籤', '019', '小吉', '<h5 class=\"text-center tm-text-gray-dark\">家道生荊棘</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">家裡的生意有阻礙，事情不按照所想地進行吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">兒孫防虎威</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">子或孫如果常聽長輩（居上位者）的話，就會沒事吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">香前祈福厚</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">抱持著強烈的信心，如果竭盡真誠的心的話，將來會有好事吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">方得免分離</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">得到福德，本來別離的命運的人（物）會在不用分別的情況下解決吧。 </h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">能實現一半吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">雖然長期得病，但是不會危及性命吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">變成遲遲地出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">大概找不回來了吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">不好也不壞吧。 </span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">節制比較好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">交往：</span><span class=\"tm-text-primary\">節制吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚：</span><span class=\"tm-text-primary\">不會到達好的結果吧。</span>\r\n\r\n</div>'),
(20, '第二十支籤', '020', '吉', '<h5 class=\"text-center tm-text-gray-dark\">月出漸分明</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是月亮也出來了，漸漸地變明朗般地，終於漸漸地希望也能被實現吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">家財每每興</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">家中的財產也能慢慢地累積，家中的生意也會繁盛起來吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">何言先有滯</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">至今為止不能暢快地運作的事情，最後也會向好的方面進行吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">更變立功名</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">更進一步地，世間的評價好，變得能得到利益吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">能被實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會來吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">全部都得到好結果吧。</span>\r\n\r\n</div>'),
(21, '第二十一支籤', '021', '吉', '<h5 class=\"text-center tm-text-gray-dark\">洗出經年否</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是至今為止的壞事已被洗去般地，變成輕鬆暢快的狀況吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">光華得再清</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">好事再度發生，更加地帶來比現在更好的結果吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">所求終吉利</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">願望都會變成好結果吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">重日照前程</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">隨著日子一天又一天的過，前途增添光輝，狀況也變好吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">能被實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">雖然會恢復但是切忌粗心大意。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚：</span><span class=\"tm-text-primary\">會得到好結果吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行、交往：</span><span class=\"tm-text-primary\">全部好吧。</span>\r\n\r\n</div>'),
(22, '第二十二支籤', '022', '吉', '<h5 class=\"text-center tm-text-gray-dark\">漸漸濃雲散</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像濃雲漸漸變無，萬里晴空般地，問題也解決，似乎好事會發生。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">看看月再明</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像月亮再出現，於澄凈的天空放光明般地，心情也輕鬆吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">逢春華菓秀</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像草木在春天生氣勃勃，花也盛開而變得色彩繽紛般地，過著充實的生活，也變得幸福吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">雨過竹重青</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像竹子也遇雨增添色彩般，如果人也連連遇好事的話，越來越能得到好結果吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">雖然能被實現，但是變得比較晚吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">因為會治好，耐心等待康復吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">雖然會出現但很遲吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、旅行、交往：</span><span class=\"tm-text-primary\">全都好吧。</span>\r\n\r\n</div>'),
(23, '第二十三支籤', '023', '吉', '<h5 class=\"text-center tm-text-gray-dark\">紅雲隨步起</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">已經能看到好的徵兆。要前往的將來會遇到好事吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">一箭中青霄</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">你也像向青空射出的箭般地作吧。這樣的話，無論什麼願望也都沒問題，會實現吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">鹿行千里遠</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">但是受到眼前的成功的影響驕傲自大的話，就會變得看不到目標吧。通盤的判斷然後行動是很重要的吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">爭知去路遙</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">因為過於自信會招致失敗，小心期望過高，誠實是很重要的事。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">雖然會實現，但是要考慮能力吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">難以康復吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不能出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難以找到吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">沒問題吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>'),
(24, '第二十四支籤', '024', '凶', '<h5 class=\"text-center tm-text-gray-dark\">三女莫相逢</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">產生不合道理的慾望等等的事，請不要做身為人不可以犯的錯誤行為吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">盟言說未通</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">光只是口頭約束，沒有實行的話，就會與對方心意無法相通。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">門裡心肝掛</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">因此，煩惱、苦悶也是不行的。抱持著信仰心吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">縞素子重重</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">不這樣的話，只會發生不吉利的事吧。因此從各種面向，好好地分辨黑白是非吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">不會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">雖然拖很長，但會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不能出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">變成到後來才找到吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">不好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">不好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">變成不好的結果吧。</span>\r\n\r\n</div>'),
(25, '第二十五支籤', '025', '吉', '<h5 class=\"text-center tm-text-gray-dark\">枯木逢春生</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像草木在春天發芽，綠葉生長茂盛般地，隨著時間繁榮昌盛吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">前途必利亨</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">去路必有好事發生，能幸福吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">亦得佳人箭</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">能邂逅很棒的人吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">乘車祿自行</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">變得能得到崇高地位、財產吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">遲遲才來吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">似乎沒問題。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行、結親緣：</span><span class=\"tm-text-primary\">全部都變成好結果吧。</span>\r\n\r\n</div>'),
(26, '第二十六支籤', '026', '吉', '<h5 class=\"text-center tm-text-gray-dark\">將軍有異聲</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">平日的指導力得到部下的信賴，大家聽你的指揮吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">進兵萬里程</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像是無論在多遠的人都尊崇將軍的命令，大批士兵向前行進般地，即使多麼困難的工作都能得到身邊的人或部下的幫助吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">爭知臨敵處</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">但是，努力急著成功都徒勞無功吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">道勝却虛名</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就算是達成了目標，也會變成白費力氣吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">會變成好結果吧。</span>\r\n\r\n</div>'),
(27, '第二十七支籤', '027', '吉', '<h5 class=\"text-center tm-text-gray-dark\">望祿應重山</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像要越過重重相連的山脈般，因為克服辛勞又痛苦的事，沒多久就能得到上天給予的幸福吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">花紅喜悅顏</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像紅色美麗的花朵已經綻放般的喜悅充滿臉上吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">舉頭看皎月</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像如果看到月亮的話，雲也消散了，清澈地能看到般地，變成是在人生上能夠發揮能力的時期吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">漸出黑雲間</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像終於從又厚又黑的雲中脫離般地，痛苦與煩惱的每天過去了，圍繞而來是沈靜安心的心情吧。但是，請多加注意，切忌粗心大意。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧，但切忌粗心大意。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">非常好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">全部都變成好結果吧。</span>\r\n\r\n</div>'),
(28, '第二十八支籤', '028', '凶', '<h5 class=\"text-center tm-text-gray-dark\">意速無船渡</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是就算心裡很著急，船卻不前進般地，只是焦急，事情沒有改變吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">波深必誤身</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是就算想勉強渡過海洋或河川，波浪又高又危險般地，就算想快點到達目的地貫徹事情，卻反而變成自己綁著自己的脖子的結果吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">切須回舊路</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">想在異鄉出人頭地，不如回到故鄉靜靜地生活比較好吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">方可免災迍</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">如果這樣的話，有災害也能逃離吧，自己也會變得安泰吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難以實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">如果長期養生的話會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">變得遲遲地才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>'),
(29, '第二十九支籤', '029', '吉', '<h5 class=\"text-center tm-text-gray-dark\">憂轗漸消融</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">悲傷或擔心的事終於消失了吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">求名得再通</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">如果想聲名遠播的話，能像以前一樣再次向身邊的人傳播名聲吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">寶財臨祿位</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">收入或地位也變成如同希望一樣實現吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">當遇主人公</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">能遇到好的長官，慢慢地向成功邁進吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">能被實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。 </span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">交往、結婚：</span><span class=\"tm-text-primary\">全都好吧。</span>\r\n\r\n</div>'),
(30, '第三十支籤', '030', '吉', '<h5 class=\"text-center tm-text-gray-dark\">仙鶴立高枝</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就算吉利的鶴想停在高高的樹枝上，沒辦法到達般地，雖有強烈的願望但阻礙很多吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">防他暗箭虧</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像在黑暗的夜晚中，要難防範不知從哪飛來的箭般地，困難是能想像得到的吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">井畔剛刀利</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就算鶴站在泉水中，因為有又硬又銳利的刀子，似乎有危險般地，似乎要發生很多妨礙、阻礙的事吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">戶內更防危</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">不單單只是外面的事，家裡的危機也是不擔心不行。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">危險吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">如果能祈求神佛的保佑，會變成勉強可以的結果吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">節制吧。</span>\r\n\r\n</div>'),
(31, '第三十一支籤', '031', '末吉', '<h5 class=\"text-center tm-text-gray-dark\">鯤鯨未變時</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">因為鯤鯨是很大的魚，不知不覺會變成大鳥。但是，因為現在尚未變化，一直抱著很大的希望，但是還不能實現的狀態吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">且守碧潭溪</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像暫時守著深青色的溪流中，等待時機的到來般地，現在天天的行動都要保守，等待幸運的到來吧。這麼做的話，時來運轉，變成大鳥的時候會到來吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">風雲興巨浪</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">掀起浪花，有著向天空飛去的沸騰的心。幸運到來，朝向成功，拼命地不斷努力是很重要的。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">一息過天涯</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">這樣做就會一口氣飛向天空吧。步向成功的道路，在世間似乎會好名聲遠播的事。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">雖能被實現，但要考慮時機吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">拖很久吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">變得遲遲地才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">變得遲遲地才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">沒阻礙吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>'),
(32, '第三十二支籤', '032', '吉', '<h5 class=\"text-center tm-text-gray-dark\">似玉藏深石</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像是不知道寶石藏深在石頭下般地，就算有才能，不在意，不努力振作施展才能的話，什麼都不能得到吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">休將故眼看</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">用不管眼前事物的方式，才能是會就這樣埋沒下去吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">一朝良匠別</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">可是如果不斷持續地努力的話，不知不覺能被良師、益友發現。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">方見寶光寒</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">然後，就像被磨光的寶石閃閃發光地出現般，能展現成果，能為社會盡心力吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">雖然會拖長，但是不會失去生命。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">變得遲遲地才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">全部和好結果有關吧。</span>\r\n\r\n</div>');
INSERT INTO `fortune poems` (`poem_id`, `poem_no`, `poem_url`, `poem_sign`, `poem_text`, `poem_meaning`) VALUES
(33, '第三十三支籤', '033', '吉', '<h5 class=\"text-center tm-text-gray-dark\">枯木逢春艷</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就算是枯木也會在春天開出美麗的花朵般地，人生就算是在嚴長的冬天裡的辛勞，有一天會得到回報吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">芳菲再發林</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是草花再次盛開，氣味也充滿林間般地，幸運再次來的時節吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">雲間方見月</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是雲散可以看見明月般地，各式各樣的困難或痛苦之間，幸運會到來吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">前遇貴人欽</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">善人（神佛、人生的前輩）的引導，漸漸地喜事增加吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">能被實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">沒有阻礙吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">全都好吧。</span>\r\n\r\n</div>'),
(34, '第三十四支籤', '034', '吉', '<h5 class=\"text-center tm-text-gray-dark\">臘木春將至</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是冬天枯萎的樹木也會有花開的春天接近般地，人生的苦難也結束，慢慢地運氣會到來吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">芳菲喜再新</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是如果春天來了的話草木也發新芽，吐芬芳，開滿花般地，能發揮才能吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">鯤鯨興巨浪</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">讓人看到大魚變成大鳥，掀起大波浪的氣勢，去推動事情吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">舉鉤祿為真</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是如果要釣魚的話就能大豐收般地，因為窮究真正的道路，你成功的時候也會到來吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">能被實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">遲吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">遲遲才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">全都和好結果相連吧。</span>\r\n\r\n</div>'),
(35, '第三十五支籤', '035', '吉', '<h5 class=\"text-center tm-text-gray-dark\">射鹿須乘箭</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像拉弓瞄準，就能按照所想地射中鹿一樣，自己的行為直接連向成功的方向吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">故僧引路歸</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像被良好的引導引領般地，能得到良好的居上位者援助和意見吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">遇道同仙籍</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">因為遵從有智慧的人的教導，能夠得到出人頭地的喜悅吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">光華映晚暉</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是花在夕陽的照耀下，越來越閃耀般地，在周圍的人對你的評價變高了吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">能被實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">變遲才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">沒有障礙吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行、結婚、交往：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>'),
(36, '第三十六支籤', '036', '末吉', '<h5 class=\"text-center tm-text-gray-dark\">先損後有益</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">即使開始的時候損失，後來一定能得到利益的，有令人高興的事吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">如月之剝蝕</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就算月亮變小，也會再變大，變回原來的滿月般地，雖然希望變薄弱了，但後來能被實現吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">玉兔待重生</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">雖然變成日食或月食的話，世界就變得黑暗，但是時間經過，馬上就回到原來的狀況。災難也隨時間過去，願望會實現吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">光華當滿室</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">月亮的光芒（希望）照進家中，變得明亮般地，充滿歡喜吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">稍後會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">雖然拖長，但可以治癒吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">遲遲地才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">應該先放棄，暫時多觀察情況再說吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">途中，似乎會有不好的事吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結親緣、喜慶、交往：</span><span class=\"tm-text-primary\">不好吧。</span>\r\n\r\n</div>'),
(37, '第三十七支籤', '037', '半吉', '<h5 class=\"text-center tm-text-gray-dark\">陰靉未能通</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像烏雲又厚又長，分不清東西般地，心裡迷惘，現在願望還無法上達天聽吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">求名亦未逢</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">雖然祈求名聞人世間，但是現在還沒遇到機會吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">幸然須有變</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">可是心中的痛苦離去，運勢展開，變得幸福的日子會來了吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">一箭中雙鴻</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是用一支箭射中兩隻鳥般地，好事似乎接踵而來。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難以實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">變成遲遲地才好轉吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">變成遲遲地才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">變成遲遲地才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">往後一點，變成好吧。</span>\r\n\r\n</div>'),
(38, '第三十八支籤', '038', '半吉', '<h5 class=\"text-center tm-text-gray-dark\">月照天書靜</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是天空沒有陰霾明顯地能看見月亮般地，沒有迷惘，澄淨的心吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">雲生霧彩霞</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">但是，至今為止看到的月亮也有雲靄，心裡也產生迷惘吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">久想離庭客</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">和親密的友人分別，暫時心沈浸在悲傷的思緒中吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">無事惹咨嗟</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">雖然沒事，但是卻因為擔心悲嘆的事很多，轉換心情吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會拖長吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">先放棄，暫時觀察情況再說吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">先放棄，暫時觀察情況吧。</span>\r\n\r\n</div>'),
(39, '第三十九支籤', '039', '凶', '<h5 class=\"text-center tm-text-gray-dark\">望用方心腹</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就算有願望也只是心裡想想而已，還沒到達要付諸行動吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">家鄉被火災</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像有家裡燒起來般地災難，接踵而來的危險。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">憂危三五度</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">悲傷或危險的事再次地持續發生吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">由損斷頭財</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">因為有失去和生命一樣重要的東西的可能，要小心謹慎吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難以實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">又壞又危險吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">避開比較好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">不好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">交往、結婚：</span><span class=\"tm-text-primary\">招致壞結果吧。</span>\r\n\r\n</div>'),
(40, '第四十支籤', '040', '末小吉', '<h5 class=\"text-center tm-text-gray-dark\">中正方成道</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">正因為什麼事都不偏頗的中正之道的正確行為，能夠消除災難吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">姦邪恐惹愆</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">一有邪念的話就會被引入壞的方向而去吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">壺中盛妙藥</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像是在藥箱中先存放著有效的藥般，不迷失去自己地珍惜真誠心吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">非久去煩煎</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">這樣做的話煩惱就會離去，災難會消去吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難以立刻地實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">雖然拖長但會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">變成遲遲才地出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">不好也不壞吧。</span>\r\n\r\n</div>'),
(41, '第四十一支籤', '041', '末吉', '<h5 class=\"text-center tm-text-gray-dark\">有物不周旋</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">雖然有很多東西但是難到手的狀況。雖然希望很豐富，但是難以實現吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">須防損半邊</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">成功一半，損失一半吧。但是不要堅持小事，想著一半已經成功事吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">家鄉煙火裡</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">有火災發生的危險。要充分地注意吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">祈福始安然</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">相信神明或佛菩薩，如果抱著堅定的心，最後會變得安泰吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難以實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會拖長吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">變成遲遲地才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">不好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">途中似乎有不好的事。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">會產生不好的結果吧。</span>\r\n\r\n</div>'),
(42, '第四十二支籤', '042', '吉', '<h5 class=\"text-center tm-text-gray-dark\">桂華春將到</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是桂花在春天飄香般地，對你也變成是運勢展開的時機吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">雲天好進程</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">人也轉運的話，有得到崇高的地位與榮譽的機會。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">貴人相遇處</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">有觀世音菩薩或有力人士的引導吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">暗月再分明</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">如陰天的月亮放晴般地，越來越會發生好事吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治癒吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">變得遲遲地才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">全都會變成好結果吧。</span>\r\n\r\n</div>'),
(43, '第四十三支籤', '043', '吉', '<h5 class=\"text-center tm-text-gray-dark\">月桂將相滿</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像月缺不久將變成滿月般地，好運全盛的時期正在接近了。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">追鹿映山溪</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">鹿就是祿。也就是說，雖然現在還沒得到崇高的地位和收入，但是有將來會得到的命運。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">貴人乘遠箭</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">箭就是弓箭。應該要尊敬的人從遠方乘箭而來的援助之暗示是表示著如果學習這個人的善行而做的話，表示能得到崇高地位與收入。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">好事始相宜</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">願望開始實現，名聲被好好地知道，會變得幸福吧。但是切忌高傲自滿。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">雖然拖長但是會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">變成遲遲地才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">變成好結果吧。</span>\r\n\r\n</div>'),
(44, '第四十四支籤', '044', '吉', '<h5 class=\"text-center tm-text-gray-dark\">盤中黑白子</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像在下圍棋當中的輸贏一樣，人生的吉或凶也還沒確定吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">一著要先機</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">勝負的事或人生都是要取得先機比什麼都還重要吧。區別善惡，如果是善道的話，筆直地著向善道而去是好的吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">天龍降甘澤</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">神明或佛菩薩會降下甘露（恩惠）的援助吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">洗出舊根基</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">用甘露水洗去過去的罪惡般地，清澈地，變成可以發揮本來的能力吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治癒吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">變成遲遲地才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">沒問題吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">全都好吧。</span>\r\n\r\n</div>'),
(45, '第四十五支籤', '045', '吉', '<h5 class=\"text-center tm-text-gray-dark\">有意興高顯</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">受到別人的信賴，得到名聲，變成能夠累積財富吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">祿馬引前程</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">如果能一直保持著善心的話，在將來的人生幸運會成為你的嚮導吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">得遇雲中箭</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">因為令人感謝的神佛的加護，會遇到幸運吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">芝蘭滿路生</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">如藥草或香草一片茂盛般地，被好人包圍，名聲在世間被知道，能夠變得幸福吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">變遲遲地才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">遲遲地才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">全都好結果相連吧。</span>\r\n\r\n</div>'),
(46, '第四十六支籤', '046', '凶', '<h5 class=\"text-center tm-text-gray-dark\">雷發震天昏</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">在烏雲裡雷聲響遍，變得像是要震撼天地般地，是非常令人擔心的狀況。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">佳人獨掩門</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">少女獨自一人，沒和人交流，孤單地的狀況。要時時警戒吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">交加文書上</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">在合約等事情，似乎會發生錯誤或訴訟。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">無事也遭迍</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">雖然說如果誠心誠意地小心謹慎的話比較好，但是過度信任自己的才能，打算做什麼行動的話就全部會變成為凶。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難以實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">現在放棄吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">在途中有不好的事吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結親緣、喜慶、交往：</span><span class=\"tm-text-primary\">會產生不好的結果吧。</span>\r\n\r\n</div>'),
(47, '第四十七支籤', '047', '吉', '<h5 class=\"text-center tm-text-gray-dark\">更望身前立</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">雖然為了早點實現願望似乎一直期待著，但是不可以過度著急。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">何期在晚成</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是大器晚成般地，延長志氣，擴大心胸，不焦急慢慢地等待成功吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">若遇重山去</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">重山是一山又一山的意思。因為辛苦地越過人生的山坡，希望也變得可以實現。必定會成功吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">財祿自相迎</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">財產和地位也變得可以到手吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">變成往後才會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">變得遲遲才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">在將來似乎有好事吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">全部都可得到好結果吧。</span>\r\n\r\n</div>'),
(48, '第四十八支籤', '048', '小吉', '<h5 class=\"text-center tm-text-gray-dark\">見祿隔前溪</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是隔著山谷看得見寶物般地，就算想要的東西就在眼前，似乎也難得到吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">勞心休更迷</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">勉強地想得到那個財寶，會使心裡迷惘，放棄吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">一朝逢好渡</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就這樣用平常心按照平日的生活吧。如果時機來的話，自然會得到財寶。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">鸞鳳入雲飛</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">有居上位者或認識的人的援助吧。像鳳凰飛向天空般地，會出人頭地，好事會來臨吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">如果一直抱持著正直的心的話，到後來能被實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">雖然拖長，但將來會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">變成遲遲才來吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">雖然開始不好，但是到後來會變好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行、結婚、交往：</span><span class=\"tm-text-primary\">馬馬虎虎吧。</span>\r\n\r\n</div>'),
(49, '第四十九支籤', '049', '吉', '<h5 class=\"text-center tm-text-gray-dark\">正好中秋月</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是十五號的夜晚，沒有月缺，又圓又明亮的月亮般地，運氣非常好吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">蟾蜍皎潔間</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像因為月亮皎潔，住在裡面的兔子和蟾蜍能清楚看見般地，展現潔淨的心和光輝吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">暗雲知何處</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是天空一點雲都沒有，萬里晴空般地，沒有妨礙的東西，心中也沒有迷惘吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">故故兩相攀</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">整個天空沒有雲般地，心中沒有比這個更澄淨的吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">嚴重吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">變成遲遲才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">馬馬虎虎吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">交往、結婚：</span><span class=\"tm-text-primary\">馬馬虎虎吧。</span>\r\n\r\n</div>'),
(50, '第五十支籤', '050', '吉', '<h5 class=\"text-center tm-text-gray-dark\">有達宜更變</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">為了達成事情，要好好地理解那件事的內容，邊改進以前的方法，邊向前進吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">重山利政逢</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">如果山重疊的話，成為出這個字。已經下了使事情達成的決心的話，從現在的地方起飛展開行動吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">前途相偶合</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">這麼做的話，在將來自然而然地能遇見幸福吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">財祿保亨通</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">再來，財寶也從一開始到結束確實地能得到吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">變遲吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">成為好的旅行吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">成為好的結果吧。</span>\r\n\r\n</div>'),
(51, '第五十一支籤', '051', '吉', '<h5 class=\"text-center tm-text-gray-dark\">修進甚功辛</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">雖然沒有懶惰，好像一直勤勞努力，但是現在只感覺到辛苦吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">勞生未得時</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">因為有大大的願望，雖然拼命地努力，但是似乎還沒到花開的時期吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">騰身遊碧漢</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">然而，看著天空，抱著打算一飛向上的大決心，認真地挑戰看看吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">方得遇高枝</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">確實地能得到資產和財寶、出人頭地等，能安心吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">變成往後才治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">變晚吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好事吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">會得到好結果吧。</span>\r\n\r\n</div>'),
(52, '第五十二支籤', '052', '兇', '<h5 class=\"text-center tm-text-gray-dark\">有僭須惹訟</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">身上發生錯誤的事，被人控訴，引起爭訟吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">兼有事交加</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">這個爭訟之外，還有一件難以解開的困難爭執的事吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">門裡防人危</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">原因之一是親戚或家人包含在其中，從這件事解決後，可以防止災難吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">災臨莫嘆嗟</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就算有災難也不要嘆氣，幫助你的人會出現吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會拖長吧。 </span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">馬馬虎虎還算有點好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">因為不好，避開吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">不好也不壞吧。</span>\r\n\r\n</div>'),
(53, '第五十三支籤', '053', '吉', '<h5 class=\"text-center tm-text-gray-dark\">久困漸能安</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">長時間的勞苦終於溶化不見，漸漸地變好吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">雲書降印權</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">從居上位者能得到好的資格（身份、或職位）或權力吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">殘花終結實</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是殘存的花朵結成果實般地，終於運氣來了吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">時亨祿自遷</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">變得能自由地得到福德，並且高昇，到最後都幸福吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">能被實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">遲遲地才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">變得遲遲才找到吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">變成好的旅行吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">全都朝向好的方向發展吧。</span>\r\n\r\n</div>'),
(54, '第五十四支籤', '054', '兇', '<h5 class=\"text-center tm-text-gray-dark\">身同意不同</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">只是著急，不能分出好壞吧。像月食般地，漸漸起烏雲，變得籠罩在黑暗般地狀態吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">月蝕暗長空</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">雖然有車但是目的地沒決定般地，就算好事在眼前，也沒辦法得到的狀態吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">輪雖常在手</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是魚沒遇到水就會死亡般地，如果和周圍的人沒辦法心意相通的話，什麼是都沒辦法做成的。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">魚水未相逢</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">經常順應這個世界、心情平靜的態度是很重要的。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難實現吧。 </span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">危險吧。 </span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。 </span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">暫時放棄，再觀察看看吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">變成壞結果吧。</span>\r\n\r\n</div>'),
(55, '第五十五支籤', '055', '吉', '<h5 class=\"text-center tm-text-gray-dark\">雲散月重明</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像遮蔽月亮的雲散去，變得更加明亮般地，被關閉的心雲放晴，還變成很澄淨的心吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">天書得誌誠</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像月亮或星星閃耀美麗光輝般地，人心也澄清，妨礙的事物也消失不見吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">雖然多阻滯</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就算有阻礙或困難，沒有到變成痛苦的程度吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">花發再重榮</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">變成再度繁盛的狀態，子孫也會繁榮吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">變遲吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會找到吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">全都好吧。</span>\r\n\r\n</div>'),
(56, '第五十六支籤', '056', '末小吉', '<h5 class=\"text-center tm-text-gray-dark\">生涯喜又憂</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是如果有喜事的話也會有悲傷的事般地，吉凶輪流來吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">未老先白頭</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">雖然還沒到那個年齡，但長出明顯易見的白髮，是因為操心的事很多吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">勞心千百度</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">會遇到好幾次重疊而來的勞苦吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">方遇貴人留</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">但是得到觀世音菩薩或居上位者的幫助，得到力量，最後變得幸福吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">變成遲遲才治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">遲遲才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">半吉吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">如果有一起同行的人的話好（安全）吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結親緣、喜慶、交往：</span><span class=\"tm-text-primary\">馬馬虎虎還可以吧。</span>\r\n\r\n</div>'),
(57, '第五十七支籤', '057', '吉', '<h5 class=\"text-center tm-text-gray-dark\">欲渡長江闊</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是雖然想要渡過長長的河川，幅寬太寬難渡過般地，就算想要達成事物，困難很多吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">波深未自儔</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是波濤凶猛、沒有船，難渡河般地，也變成達成困難的狀態吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">前津逢浪靜</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">所以在波浪平靜、容易渡河的時候到來為止，靜靜地等待吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">重整鉤鰲鉤</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">一度冷靜之後，如果是準備好釣鉤的你的話，就能得到能釣大魚的契機吧。這樣得到幸運的機會會到來吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">雖然會治好，但會拖長吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">變得遲遲才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">馬虎虎還可以吧。</span>\r\n\r\n</div>'),
(58, '第五十八支籤', '058', '兇', '<h5 class=\"text-center tm-text-gray-dark\">有徑江海隔</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">即使有想要走的道路，就像遇到海或河的阻隔般地，要實際執行事情，有各式各樣的困難吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">車行峻嶺危</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是押著車向險峻的山走去，似乎有非常大的困難。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">亦防多進退</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">不能粗心大意。無論向前進、往後回都沒有辦法的困難的事情吧。要小心吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">猶恐小人虧</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">有會進行阻礙的人，壞的時候，壞事接踵而來。要經常小心吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">不能安心吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">中止吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行、結婚、交往：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>'),
(59, '第五十九支籤', '059', '兇', '<h5 class=\"text-center tm-text-gray-dark\">去住心無定</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">有飄移不定的心，沒有決定（沒有決心）吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">行藏亦未寧</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">只是對事物迷惘，充滿各種不安吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">一輪清皎潔</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">雖然心中的明月清澈，明亮地閃耀光輝，但被迷惘的烏雲覆蓋，僅僅一點點的未來也不能看見吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">却被黑雲乘</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">快點除去迷惘的雲，取回原本的心吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">危險吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">似乎會發生壞事吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">交往、結婚：</span><span class=\"tm-text-primary\">得到壞結果吧。</span>\r\n\r\n</div>'),
(60, '第六十支籤', '060', '吉', '<h5 class=\"text-center tm-text-gray-dark\">高危安可涉</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像是高處的風很強般地，過於引人注目反而過得不安穩。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">平坦是延年</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">正因為平凡的生活，才是長壽或安樂的方法。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">守道當逢泰</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">如果能守著這樣的生活方式，會是安泰又平和吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">風雲不偶然</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">這樣的誠實的生活必定會得到上天的恩惠吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">如果正心而行的話會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會拖長吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">不好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">馬馬虎虎地算好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">馬馬虎虎地算好吧。</span>\r\n\r\n</div>'),
(61, '第六十一支籤', '061', '半吉', '<h5 class=\"text-center tm-text-gray-dark\">舊愆何日解</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">變成擔心過去的錯誤何時才能消失吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">戶內保嬋娟</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">家裡加入美人，這件事想向世人公布就是沒有反省自己的生活與沒有節操，用心在家裡和睦吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">要逢十一口</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">如果試著將十一和口重疊起來看的話，會變成吉字。也就是說，如果期望吉事而努力做的話，必定會到來吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">遇鼠過牛邊</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是在人們沈靜入睡的夜晚也起床工作般地努力吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會拖長吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">變遲吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難找到吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">不好吧。</span>\r\n\r\n</div>'),
(62, '第六十二支籤', '062', '大吉', '<h5 class=\"text-center tm-text-gray-dark\">災轗時時退</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">災難也慢慢地消失，運勢會展開來吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">名顯四方揚</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">名聲慢慢地傳遍世間，就好的意義看，不知道的人也變得不存在吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">改故重乘祿</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">能改去過去的事，名聲和實際都能得到幸運吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">昴高福自昌</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">出人頭地，變得福運繁榮，會繁盛吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">沒問題吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">全都好吧。</span>\r\n\r\n</div>'),
(63, '第六十三支籤', '063', '兇', '<h5 class=\"text-center tm-text-gray-dark\">何故生荊棘</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">理由也一直不知道地，家中產生問題吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">佳人意漸疏</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">家人或夫婦之間心意無法相通吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">久困重輪下</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">不反省原因的話就會像被埋在重重地車輪底下般地，變得長期間地辛勞吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">黃金未出渠</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">因為玩樂失去財產，一直無法回復。請努力忠於自己的本分吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">難治癒吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">難找到吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">似乎會發生壞事吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">會產生壞結果吧。</span>\r\n\r\n</div>'),
(64, '第六十四支籤', '064', '兇', '<h5 class=\"text-center tm-text-gray-dark\">安居且慮危</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就算是似乎安樂，心裡也還留著憂慮吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">情深主別離</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">和感情深厚的人也有可能會發生離別的悲傷的事。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">風飄波浪急</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">這簡直是風強浪大般的狀況啊。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">鴛鴦各自飛</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">雖然鴛鴦不分離地飛行，但是飛向分離的命運。靜靜地等待吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">危險吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">還可以吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">還可以吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">不好吧。</span>\r\n\r\n</div>');
INSERT INTO `fortune poems` (`poem_id`, `poem_no`, `poem_url`, `poem_sign`, `poem_text`, `poem_meaning`) VALUES
(65, '第六十五支籤', '065', '末吉', '<h5 class=\"text-center tm-text-gray-dark\">苦病兼防辱</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">覺得內心的痛苦和受人侮辱，不安穩吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">乘危亦未穌</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像面臨危險的人還沒完時甦醒般地，這個困難沒有那麼簡單地解決。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">若見一陽後</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">如果春天來了的話（如果能有觀世音菩薩的慈悲光輝的話）也會有好事吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">方可作良圖</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">從好事開始發生起，請立定計畫開始行動。到好事開始為止請靜靜地忍耐。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">以後會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">雖然拖長但會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">變得遲遲才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">安全吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">得到還可以的好結果吧。</span>\r\n\r\n</div>'),
(66, '第六十六支籤', '066', '兇', '<h5 class=\"text-center tm-text-gray-dark\">水滯少波濤</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">水淤塞、浪不起可說是物質、精神貧乏、也沒有辦法從事社會活動的狀態。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">飛鴻落羽毛</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像是羽毛掉落，變成無法飛行的鳥般地，失去重要的東西，沒辦法生活吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">重憂心緒亂</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">悲傷的事接連而來，心思混亂，方法用盡不知如何是好吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">閑事惹風騷</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就算想待在安靜的地方，也會有引來大問題吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">可疑，不明朗吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結親緣、喜慶、交往：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>'),
(67, '第六十七支籤', '067', '兇', '<h5 class=\"text-center tm-text-gray-dark\">枯木未生枝</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">變成枯木，枝幹上沒有長葉子，似乎現在還沒有迎接春天般地，就算要有執行不會實現的願望，心中只是越來越煩惱而已。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">獨步上雲岐</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">不瞭解自己的身份、地位，就要有所行動，是無法安穩的。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">豈知身未穩</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">要有信心，要舉止謹慎吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">獨自惹閑非</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">不悔悟自己的到前幾日為止錯誤，以後就會後悔吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會拖長吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">多比較看看比較好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">不好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>'),
(68, '第六十八支籤', '068', '吉', '<h5 class=\"text-center tm-text-gray-dark\">異夢生英傑</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">生出優秀的人、做好夢是因為神佛加持庇護的幸運到來的暗示。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">前來事可疑</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">雖然得到這個幸福，但和昨天的自身相比較，卻難以置信吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">芳菲春日暖</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像春天變溫暖，飄散蔬菜花的香味般地，幸福來臨。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">依舊發殘枝</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">枯了的樹木也會開花般地，似乎會發生可喜可賀的事。只是，自重自愛是非常重要的。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出來吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">全都好吧。</span>\r\n\r\n</div>'),
(69, '第六十九支籤', '069', '兇', '<h5 class=\"text-center tm-text-gray-dark\">明月暗雲浮</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">亮的月亮也被烏雲遮住，變為不晴朗的狀態吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">花紅一半枯</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是紅花有一半枯掉般地，運氣也變成走下坡。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">惹事傷心處</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">雖然打算努力做事，也盡是心痛、擔心吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">行舟莫遠圖</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像要乘船等出去遠方的大希望，無法渡過般地，謹慎地等待時機到來吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">切忌粗心大意。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">放棄吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">交往、結婚：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>'),
(70, '第七十支籤', '070', '兇', '<h5 class=\"text-center tm-text-gray-dark\">雷發庭前草</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">雷落在自家的庭前是由於身份低下或雇用的人引發災禍。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">炎火向天飛</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是火焰向天飛去般地，上下關係變得不合，有爭執吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">一心來趕祿</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">一心一意想要得到利益但沒辦法實現。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">爭奈掩朱扉</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">至少要重視雇用的人或下屬等，防止災難發生吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會拖長吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">避免吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行、結婚、交往：</span><span class=\"tm-text-primary\">萬事壞吧。</span>\r\n\r\n</div>'),
(71, '第七十一支籤', '071', '兇', '<h5 class=\"text-center tm-text-gray-dark\">道業未成時</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">因為心裡和工作的實力都還沒成熟，現在是學習的時期。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">何期兩不宜</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">因為心裡和工作的實力都還沒成熟，就算想做什麼也無法按照所想地順利進行吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">事煩心緒亂</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">因為這樣的事，引起各種麻煩，令人厭煩的痛苦和混亂吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">飜做徘徊思</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">沒辦法好好地想事情，只是晃來晃去心情不定吧。需要反省。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會拖長吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、旅行、交往：</span><span class=\"tm-text-primary\">產生壞的結果吧。</span>\r\n\r\n</div>'),
(72, '第七十二支籤', '072', '吉', '<h5 class=\"text-center tm-text-gray-dark\">戶內防重厄</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">家中恐怕有災禍到來。但是，為了防止發生，要小心吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">花菓見分枝</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">枝幹各自分開是說家庭不合。因為這個緣故全部都不和睦吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">嚴霜纔過後</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">相互地悔改壞的地方，然後戰勝這的試煉。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">方可始相宜</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">如果這樣做的話，似乎會以好事發生。因為家裡和睦，好事似乎越變越多。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">後來會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會拖長吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">遲遲才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">變成遲遲才能找到吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">還算好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">沒有特別的阻礙吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">雖然還算好，但最後變得更好吧。</span>\r\n\r\n</div>'),
(73, '第七十三支籤', '073', '吉', '<h5 class=\"text-center tm-text-gray-dark\">久暗漸分明</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">久佈烏雲的天空也終於放晴，很暢快吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">登江綠水澄</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">幸運似乎會到來。水或樹木都澄淨明朗，也變得沒有要擔心的事吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">芝書從遠降</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">因為得到居上位者的推薦，出人頭地吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">終得異人成</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">被神佛幫助，得到好結果吧。要有信心是很重要的。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">變得往後才能被實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出來吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">全都好吧。</span>\r\n\r\n</div>'),
(74, '第七十四支籤', '074', '兇', '<h5 class=\"text-center tm-text-gray-dark\">蛇虎正交羅</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">因為不知道蛇或虎混雜等的道理，會發生壞事。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">牛生二尾多</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">牛加上兩條尾巴的話，就變成失這個字。說不定會有什麼損失。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">交歲方成慶</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">雖然隨著年齡增加透過交流，有令人高興的事，但是卻往往常有各種爭奪、爭吵的事。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">上下不能和</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">因為家裡不和睦而不會平靜。用溫和的心領會忍耐吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難以實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">不能安心吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、旅行、交往：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>'),
(75, '第七十五支籤', '075', '兇', '<h5 class=\"text-center tm-text-gray-dark\">孤舟欲過岸</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">一艘小船想要航向遙遠的對岸，會是沒有幫忙的人的狀態。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">浪急渡人空</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">因為水流急，要渡河是過度危險的事，所以是虛幻的心情。各種障礙滾滾而來，什麼事都困難的吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">女人立流水</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">柔弱的女性一人站在急流處，是非常危險的。雖然想為她作些什麼但是處於無可奈何的狀態。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">望月意情濃</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">相對於自己的擔心，非常羨慕他人的安樂吧，但是要反省自己的行為，竭盡地拜託神佛，防止災難發生吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">陷入的話（若是病人的話）危險吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">不能找回來吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>'),
(76, '第七十六支籤', '076', '吉', '<h5 class=\"text-center tm-text-gray-dark\">富貴天之祐</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">得到財產、地位變高是上天賜給的東西。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">何須苦用心</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就算用盡心思與痛苦，沒有神佛的幫忙的話，不能實現吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">前程應顯跡</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">前程的好壞是根據至今為止所做的行為的好壞而來的吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">久用得高臨</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">長時間實行正道、徹底行善等等的話，地位會變高，財寶也能得到吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出來吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結親緣、喜慶、交往：</span><span class=\"tm-text-primary\">全部都好吧。</span>\r\n\r\n</div>'),
(77, '第七十七支籤', '077', '兇', '<h5 class=\"text-center tm-text-gray-dark\">累滯未能穌</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">萬事不能順利進行會接連而來，看不到前景吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">求名莫遠圖</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">現在不能想要名聲廣播、祈求幸福等。想要乘船而去，但浪高難以渡過吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">登舟波浪急</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">雖然願望好像快要實現，但是被災難阻擋。結果的差別像天和地般地不一樣的狀況。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">咫尺隔天衢</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">請自重，等待時機的到來吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">不能安心吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難找到吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>'),
(78, '第七十八支籤', '078', '大吉', '<h5 class=\"text-center tm-text-gray-dark\">但存公道正</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\"> 守著公共的正道，比起為了自己，更要為了大家做好事吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">何愁理去忠</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">忠實地盡心盡力做事，就算這樣反而事情不能順利進行、立場變壞也要當作沒有悲傷嘆息的事吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">松柏蒼蒼翠</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像松樹或柏樹經常青翠般地，人的心也要注意要經常走在誠懇的道路吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">前山祿馬重</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">到最後會有好事，福德像是滿出來般地變得幸福吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出來吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">全都好吧。</span>\r\n\r\n</div>'),
(79, '第七十九支籤', '079', '吉', '<h5 class=\"text-center tm-text-gray-dark\">殘月未還光</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是明亮的月光還沒衰退、光輝閃耀著般地，你也隨著上年紀越來越能活躍。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">樽前非語傷</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像是就算喝酒，一點點也不會混亂般地，什麼壞事都沒做。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">戶中有人厄</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">但是，家中稍微有災難吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">祈福保青陽</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">如果懷著信心，期禱幸福的話，像是溫暖的日光照射的春天般地，心變得安定、安泰吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">雖然能被實現但是大願望不行吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">雖然會拖長，但會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">變遲才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">遲遲地才找到吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">交往、結婚：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>'),
(80, '第八十支籤', '080', '大吉', '<h5 class=\"text-center tm-text-gray-dark\">深山多養道</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">惡的修行是要窮究真誠之道吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">忠正帝王宣</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">忠誠的心被認同、居上位者派人過來，變成大大地出人頭地吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">鳳遂鸞飛去</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">鳳凰也是鸞也是，是出現在正確的人世間的鳥。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">昇高過九天</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">飛越極高的天空，還登上比這樣更高吧。因為努力，高高的目標陸陸續續地能達成吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">是好的，因為全部都保持謙虛的姿態，所以會招來好結果吧。</span>\r\n\r\n</div>'),
(81, '第八十一支籤', '081', '小吉', '<h5 class=\"text-center tm-text-gray-dark\">道合須成合</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">如果自身的行為符合道理的話，什麼都會成功吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">先憂事更多</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">但是在最初的時候悲傷和痛苦會很多吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">所求財寶盛</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">之後，所希望的財寶會如心想地聚集過來吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">更變得中和</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">未來的災難也會馬上變成往幸福的方向，繁盛會到來吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">如果保持端正的心的話，會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>'),
(82, '第八十二支籤', '082', '兇', '<h5 class=\"text-center tm-text-gray-dark\">火發應連天</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">有天井也燒焦般的大火是因為慾望或怒氣而不能安穩的意思。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">新愁惹舊愆</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">新的悲傷或痛苦、然後舊傷痕等被拿出來，令人擔心的事很多吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">欲求千里外</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就算想逃到遠處但是相當地困難吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">要渡更無船</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">前往的目的地有大河，就算想渡河而過但沒有船會相當辛苦。暫時保持現狀吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">可疑，不明朗吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">遲遲才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">多比較看看比較好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">全都壞吧。</span>\r\n\r\n</div>'),
(83, '第八十三支籤', '083', '兇', '<h5 class=\"text-center tm-text-gray-dark\">舉步出雲端</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">想抬腳乘雲登天，卻不能登天。高高的願望是沒有用的事。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">高枝未可攀</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">想爬上高高的樹枝，但是抓得到的樹枝，像是沒有比這個更高的一樣，自己的生活不安，也沒有援助吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">昇頭看皎月</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">抬頭看天空，就算想看見晴朗的月亮。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">猶在黑雲間</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">自己想看的月亮還隱藏在雲中，不能好好看見。安靜地端正行為吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會拖長吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">危險吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">產生壞結果吧。</span>\r\n\r\n</div>'),
(84, '第八十四支籤', '084', '兇', '<h5 class=\"text-center tm-text-gray-dark\">否極方無泰</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">八方堵塞，沒有暢通的道路吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">花開值晚秋</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">雖然花會開但是受晚秋般地冷風吹拂，立刻就枯萎了吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">人情不調備</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">各自為了自己的主張，人情心也不一致，抱怨不停吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">財寶鬼來偷</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">儲存的寶物或財產也因魔鬼出現偷走，財產漸漸地減少，於是變得貧窮吧。 </h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">危險吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">不好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">全部都產生壞的結果吧。</span>\r\n\r\n</div>'),
(85, '第八十五支籤', '085', '大吉', '<h5 class=\"text-center tm-text-gray-dark\">望用何愁晚</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">雖說願望很晚才會實現，不悲傷嘆息慢慢地等待吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">求名漸得寧</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">漸漸地名聲揚起，變成安心的心情吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">雲梯終有望</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">大大的願望也得到援助，終於能被實現吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">歸路入蓬瀛</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">變成能得到福德、財產、長壽吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">變成往後才會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>'),
(86, '第八十六支籤', '086', '大吉', '<h5 class=\"text-center tm-text-gray-dark\">花發應陽臺</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是從照到陽光的房屋，可以充分地看見花盛開的景象般地，一切都能如願地充分被能實現吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">車行進寶財</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">像是堆滿財寶的車子來到自己的家般地，財產或寶物變得豐富吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">執文朝帝殿</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">顯現學問的德行，被居上位者帶領，還讓你能說出願望吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">走馬聽聲雷</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">得意洋洋地騎馬，那種姿態是變成成令人羨慕的身份吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結親緣、喜慶、交往：</span><span class=\"tm-text-primary\">全都好吧。</span>\r\n\r\n</div>'),
(87, '第八十七支籤', '087', '大吉', '<h5 class=\"text-center tm-text-gray-dark\">鑿石方逢玉</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">偶然挖出石頭中的寶石可說是遇見沒想過的幸福。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">淘沙始見金</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像是淘砂收集金子般地，在生活中財寶自然地累積吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">青霄終有路</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">想向天空實現大成功之道，在世間也廣為稱讚吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">只恐不堅心</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">只是，如果不意志堅定一心努力的話，什麼是都不能成功吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">能被實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>'),
(88, '第八十八支籤', '088', '兇', '<h5 class=\"text-center tm-text-gray-dark\">作事不和同</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就算想做些什麼事，卻和人或者夫婦之間是不和睦的狀態。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">臨危更主凶</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">因為在這樣的狀態下，就算想從危險的事逃離、防止危險的事，壞事還是發生吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">佳人生苦根</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">產生像是關係到自己的妻子般的痛苦或擔心的事。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">閑慮兩三重</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">壞事一而再再而三地接踵發生。要非常有信心吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">危險吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>'),
(89, '第八十九支籤', '089', '大吉', '<h5 class=\"text-center tm-text-gray-dark\">一片無瑕玉</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">玉石因為琢磨終於變成無瑕疵的玉是世上天賜給的東西。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">從今好琢磨</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像天生的好人也因雕琢的方式不同變成更好的人般地，誠心地更加努力吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">得遇高人識</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">這樣做的話會被名聲高的人賞識，變成能得到提拔吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">方逢喜氣多</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">智慧或財寶充滿，被無限的欣喜包圍的時機會機到來吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">能被實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">交往、結婚：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>'),
(90, '第九十支籤', '090', '大吉', '<h5 class=\"text-center tm-text-gray-dark\">一信向天飛</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">如果有真誠心的話，最後會傳達給上天，出人頭地的時候會到來吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">秦川舟自歸</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">因為上天的恩惠，會得到各種財寶吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">前途成好事</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">將來會遇見好事吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">應得貴人推</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">因為得到居上位者（菩薩）的力量，能被吸引往好的方向吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">全部都好吧。</span>\r\n\r\n</div>'),
(91, '第九十一支籤', '091', '小吉', '<h5 class=\"text-center tm-text-gray-dark\">改變前途去</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">如果改進到現在為止壞事，嶄新地向前進的話，事情會改變的吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">月桂又逢圓</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像缺月也會再變圓般地，吉事就猶如月的圓缺。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">雲中乘祿至</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">至今為止的缺點也會漸漸地變好吧。從天降下來福德、幸運吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">凡事可宜先</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">在做任何事時，也不要落於人後，先做就能得到好結果吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">如果守正道的話會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>'),
(92, '第九十二支籤', '092', '吉', '<h5 class=\"text-center tm-text-gray-dark\">自幼常為旅</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">從小時候起的到流落旅行就是內心的不安定，勞苦很多。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">逢春駿馬驕</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">如果變成春天的話，就像馬也振奮地來回奔跑般底，終於運氣到來吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">前程宜進步</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就算如心想地去了哪裡，會變成如自己想的一樣吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">得箭降青霄</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">箭筆直地向前進時是要射中獵物。從天而來的幸運向你而來吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">會實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>'),
(93, '第九十三支籤', '093', '吉', '<h5 class=\"text-center tm-text-gray-dark\">有魚臨旱池</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">雖然有很多魚但是池子裡如果沒有水會死掉吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">跳躍入波濤</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">如果沒有保持著真誠心的話，人總有一天會向滅絕而去吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">隔中須有望</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像是在沒水池子裡的魚如果進入廣大的河川的話，也生氣勃勃般地，你的運勢也終於到來了吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">先且慮塵勞</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">但是因為有阻礙，要十分注意吧。暫時忍耐吧。因為忍耐，來生、後世或子孫能幸福吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">變成到後來能實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會拖長吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">變遲吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">馬馬虎虎還算可以吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>'),
(94, '第九十四支籤', '094', '半吉', '<h5 class=\"text-center tm-text-gray-dark\">事忌樽前語</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">喝酒時說的話是邊喝酒邊說出的話，不能全部當真。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">人防小輩交</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">避免和比自己低下的人交流吧。應該要避免只說場面話吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">幸乞陰公祐</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">好好遵從母親（神、佛）吧。如果這樣做的話，願望能被實現吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">方免事敵爻</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">化解對事物的敵對意識，廣泛和人交往吧。漸漸地會變成幸福吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">不能按照所想的實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">雖然會拖長但是不會危害生命吧。要特別用心維持健康吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">不好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">馬馬虎虎還算可以吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">勉強還算可以吧。</span>\r\n\r\n</div>'),
(95, '第九十五支籤', '095', '吉', '<h5 class=\"text-center tm-text-gray-dark\">志氣勤修業</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">立定志向，如果一心努力的話，漸漸地會朝向好的方向吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">祿位未造逢</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">現在就算福運還沒到來，必定之後會到來吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">若聞金雞語</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像等待著黑夜放明，雞開始啼叫般地，等待時機的到來吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">乘船得便風</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">時機到來、就像風把船推向前進的方向般地，漸漸地朝向幸福的方向吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">能被實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">變成遲遲才治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">遲遲地才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">變成遲遲地才出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">沒有阻礙吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>'),
(96, '第九十六支籤', '096', '大吉', '<h5 class=\"text-center tm-text-gray-dark\">雞逐鳳同飛</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像雞一邊追著鳳凰，也和鳳凰一樣地飛翔般地，仰賴有地位的人，出人頭地吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">高林整羽儀</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">然後，就像停在高高的樹林，整理羽毛般地，在和身份高的人交流中能得到幸福吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">棹舟須濟岸</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像船撐篙要渡向對岸一般地，因為自己開始行動，在世間能安心地渡過吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">寶貨滿船歸</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">出人頭地，就像財寶也很多，堆滿船歸來般地，約定了幸福。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">能被實現吧。但是，抱持全面謹慎的心是很重要的。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結親緣、喜慶、交往：</span><span class=\"tm-text-primary\">全部好吧。</span>\r\n\r\n</div>');
INSERT INTO `fortune poems` (`poem_id`, `poem_no`, `poem_url`, `poem_sign`, `poem_text`, `poem_meaning`) VALUES
(97, '第九十七支籤', '097', '兇', '<h5 class=\"text-center tm-text-gray-dark\">霧罩重樓屋</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像高高房子也隱於霧中看不見般地，煩惱的事不斷，每天昏暗、陰天吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">佳人水上行</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像柔弱的女性獨自一人乘船在水上旅行般地，現在正面臨危險的狀態。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">白雲歸去路</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">白雲未定，去的方向也不知道。每天不知道會發生什麼事吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">不見月波澄</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像澄清的水應該映著月亮的倒影也因為波浪凶猛看不見般地，種種妨礙很多吧。首先要內心安定是很重要的。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難以實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">危險吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">不好吧。 </span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>'),
(98, '第九十八支籤', '098', '兇', '<h5 class=\"text-center tm-text-gray-dark\">欲理新絲亂</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像想回復糾纏在一起混亂的絲線是困難的般地，想要除去心中的痛苦是苦難吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">閑愁足是非</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">獨自靜靜地抱著很多煩惱或悲傷，就連事物的善惡也以難發現吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">只困羅網裡</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像魚被困在網中身體不能動般地，掙扎會感到痛苦吧。。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">相見幾人悲</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">自己還有身邊的人也悲傷、煩惱的事很多，接踵不斷吧。然而如果用信心的去作話，能逃離吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難以實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">可疑，不明朗吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">不會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>'),
(99, '第九十九支籤', '099', '大吉', '<h5 class=\"text-center tm-text-gray-dark\">紅日當門照</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">就像朝陽閃耀照在門前，變得明亮般地，有因上天的恩惠而來的好事吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">暗月再重圓</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">到現在為止的黑夜中月亮再度變成滿月，普照四周吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">遇珍須得寶</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">開朗的心情會讓周遭和睦、平靜吧。能得到稀奇的財寶吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">頗有稱心田</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">變得有名，變成心願實現吧。要小心謹慎粗心大意和驕傲的事。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">能被實現吧。在萬事中要用謙虛的心吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">會治好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">會出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">交往、結婚：</span><span class=\"tm-text-primary\">好吧。</span>\r\n\r\n</div>'),
(100, '第一百支籤', '100', '兇', '<h5 class=\"text-center tm-text-gray-dark\">祿走白雲間</h6>\r\n\r\n<h6 class=\"text-center tm-text-gray\">幸福的事也隱藏在雲中而失去，是失去倚賴吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">攜琴走遠山</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">拿著琴走往山中的樣子是表示捨去人世的意思。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">不遇神仙面</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">在走往山中的途中，沒有遇到仙人傳授道理，心中不安吧。</h6>\r\n\r\n<h5 class=\"text-center tm-text-gray-dark\">空惹意闌珊</h5>\r\n\r\n<h6 class=\"text-center tm-text-gray\">心中空虛、發呆，方法用盡不知如何是好吧。更換心情，不要抱著太大的野心地在人世間過生活吧。</h6>\r\n', '<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">願望：</span><span class=\"tm-text-primary\">難實現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">疾病：</span><span class=\"tm-text-primary\">危險吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">盼望的人：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">遺失物：</span><span class=\"tm-text-primary\">難出現吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">蓋新居、搬家：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">旅行：</span><span class=\"tm-text-primary\">壞吧。</span>\r\n\r\n</div>\r\n<div class=\"mr-4 mb-2\">\r\n<span class=\"tm-text-gray-dark\">結婚、交往：</span><span class=\"tm-text-primary\">全部壞吧。</span>\r\n\r\n</div>');
